import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./FindJobs.css";

const FindJobs = () => {
  const [jobs, setJobs] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [filters, setFilters] = useState({
    category: "",
    budget: "",
    jobType: "",
  });
  const [currentPage, setCurrentPage] = useState(1);
  const jobsPerPage = 5;

  // Sample job data (replace with data from your backend)
  const sampleJobs = [
    {
      id: 1,
      title: "Web Developer Needed",
      description: "Looking for a skilled web developer to build a responsive website.",
      category: "Web Development",
      budget: "$500 - $1000",
      postedDate: "2023-10-01",
      jobType: "Full-Time",
    },
    {
      id: 2,
      title: "Graphic Designer for Logo Design",
      description: "Need a creative graphic designer to design a logo for my brand.",
      category: "Graphic Design",
      budget: "$200 - $500",
      postedDate: "2023-10-02",
      jobType: "Part-Time",
    },
    {
      id: 3,
      title: "Mobile App Developer",
      description: "Hiring a mobile app developer to create a cross-platform app.",
      category: "Mobile Development",
      budget: "$1000 - $2000",
      postedDate: "2023-10-03",
      jobType: "Contract",
    },
    // Add more jobs as needed
  ];

  useEffect(() => {
    // Fetch jobs from your backend API here
    setJobs(sampleJobs);
  }, []);

  // Filter jobs based on search query and filters
  const filteredJobs = jobs.filter((job) => {
    const matchesSearch = job.title.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = filters.category ? job.category === filters.category : true;
    const matchesBudget = filters.budget ? job.budget === filters.budget : true;
    const matchesJobType = filters.jobType ? job.jobType === filters.jobType : true;

    return matchesSearch && matchesCategory && matchesBudget && matchesJobType;
  });

  // Pagination logic
  const indexOfLastJob = currentPage * jobsPerPage;
  const indexOfFirstJob = indexOfLastJob - jobsPerPage;
  const currentJobs = filteredJobs.slice(indexOfFirstJob, indexOfLastJob);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="find-jobs-container">
      <h1>Find Jobs</h1>

      {/* Search Bar */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search jobs..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button>Search</button>
      </div>

      {/* Filters */}
      <div className="filters">
        <select
          value={filters.category}
          onChange={(e) => setFilters({ ...filters, category: e.target.value })}
        >
          <option value="">All Categories</option>
          <option value="Web Development">Web Development</option>
          <option value="Graphic Design">Graphic Design</option>
          <option value="Mobile Development">Mobile Development</option>
        </select>

        <select
          value={filters.budget}
          onChange={(e) => setFilters({ ...filters, budget: e.target.value })}
        >
          <option value="">All Budgets</option>
          <option value="$200 - $500">$200 - $500</option>
          <option value="$500 - $1000">$500 - $1000</option>
          <option value="$1000 - $2000">$1000 - $2000</option>
        </select>

        <select
          value={filters.jobType}
          onChange={(e) => setFilters({ ...filters, jobType: e.target.value })}
        >
          <option value="">All Job Types</option>
          <option value="Full-Time">Full-Time</option>
          <option value="Part-Time">Part-Time</option>
          <option value="Contract">Contract</option>
        </select>
      </div>

      {/* Job Listings */}
      <div className="job-listings">
        {currentJobs.length > 0 ? (
          currentJobs.map((job) => (
            <div key={job.id} className="job-card">
              <h2>{job.title}</h2>
              <p>{job.description}</p>
              <div className="job-details">
                <span>Category: {job.category}</span>
                <span>Budget: {job.budget}</span>
                <span>Posted: {job.postedDate}</span>
                <span>Job Type: {job.jobType}</span>
              </div>
              <Link to={`/job/${job.id}`} className="apply-button">
                Apply Now
              </Link>
            </div>
          ))
        ) : (
          <p>No jobs found.</p>
        )}
      </div>

      {/* Pagination */}
      <div className="pagination">
        {Array.from({ length: Math.ceil(filteredJobs.length / jobsPerPage) }, (_, i) => (
          <button key={i + 1} onClick={() => paginate(i + 1)}>
            {i + 1}
          </button>
        ))}
      </div>
    </div>
  );
};

export default FindJobs;
