import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { auth, db } from "../firebase"; // Import db from firebase
import { doc, getDoc } from "firebase/firestore"; // Import Firestore functions
import "./Header.css";

const Header = () => {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null); // Store the user's role
  const [loading, setLoading] = useState(true); // Loading state
  const navigate = useNavigate();

  useEffect(() => {
    if (user) {
      const fetchUserRole = async () => {
        const userDoc = await getDoc(doc(db, "users", user.uid));
        if (userDoc.exists()) {
          setRole(userDoc.data().role); // Fetch role from Firestore
        }
        setLoading(false); // Stop loading
      };
      fetchUserRole();
    } else {
      setRole(null);
      setLoading(false);
    }
  }, [user]);

  const handleProfileClick = () => {
    if (role) {
      navigate(`/dashboard?role=${role}`); // Navigate to dashboard with role
    }
  };


  return (
    <header className="header">
      <div className="navTitle">Book My Service</div>
      <nav className="navLinks">
        <Link to="/FindFreelancers" className="navLink">
          Hire Freelancer
        </Link>
        <Link to="/FindJobs" className="navLink">
          Find Jobs
        </Link>
        <Link to="/how-it-works" className="navLink">
          How it Works
        </Link>
        <Link to="/about-us" className="navLink">
          About Us
        </Link>
      </nav>
      {loading ? (
        <div className="loading">Loading...</div> // Show loading state
      ) : user ? (
        <div className="profileIcon" onClick={handleProfileClick}>
          ðŸ‘¤
        </div>
      ) : (
        <Link to="/SignIn" className="loginButton">
          Login
        </Link>
      )}
    </header>
  );
};

export default Header;
