import React, { useState } from 'react';
import { auth, db } from '../firebaseConfig';

function SignUp() {
  const [userData, setUserData] = useState({
    name: '', email: '', password: '', role: 'freelancer', skills: '', experience: '', rate: '', portfolio: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSignUp = (e) => {
    e.preventDefault();
    auth.createUserWithEmailAndPassword(userData.email, userData.password)
      .then((userCredential) => {
        db.collection('users').doc(userCredential.user.uid).set({
          ...userData,
          createdAt: new Date(),
        });
      })
      .catch((error) => console.error('Error signing up:', error));
  };

  return (
    <form onSubmit={handleSignUp}>
      {/* Collect additional fields */}
      <input name="skills" placeholder="Skills" onChange={handleInputChange} required />
      <input name="experience" placeholder="Experience" onChange={handleInputChange} required />
      <input name="rate" placeholder="Hourly Rate" onChange={handleInputChange} required />
      <input name="portfolio" placeholder="Portfolio Link" onChange={handleInputChange} />
      <button type="submit">Sign Up</button>
    </form>
  );
}

export default SignUp;
