import React, { useState } from 'react';
import { auth, db } from '../firebaseConfig';
import { useNavigate } from 'react-router-dom';

function SignUp() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [role, setRole] = useState('client'); // Default role is client
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSignUp = (e) => {
    e.preventDefault();

    // Firebase authentication: create user
    auth.createUserWithEmailAndPassword(email, password)
      .then((userCredential) => {
        const user = userCredential.user;

        // Save basic user profile to Firestore
        db.collection('users').doc(user.uid).set({
          name,
          email,
          role,  // client or freelancer
          createdAt: new Date(),
        });

        console.log('User signed up and profile saved to Firestore');
        // Redirect to account setup page for additional profile details
        navigate('/account-setup');
      })
      .catch((err) => setError(err.message));
  };

  return (
    <div className="sign-up-page container mt-5">
      <h1 className="mb-4">Sign Up</h1>
      <form onSubmit={handleSignUp}>
        <div className="form-group mb-3">
          <label htmlFor="name">Name</label>
          <input 
            type="text" 
            id="name" 
            className="form-control"
            value={name} 
            onChange={(e) => setName(e.target.value)} 
            required 
          />
        </div>
        <div className="form-group mb-3">
          <label htmlFor="email">Email</label>
          <input 
            type="email" 
            id="email" 
            className="form-control"
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            required 
          />
        </div>
        <div className="form-group mb-3">
          <label htmlFor="password">Password</label>
          <input 
            type="password" 
            id="password" 
            className="form-control"
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
            required 
          />
        </div>
        <div className="form-group mb-3">
          <label htmlFor="role">Role</label>
          <select 
            id="role" 
            className="form-control"
            value={role} 
            onChange={(e) => setRole(e.target.value)} 
            required
          >
            <option value="client">Client</option>
            <option value="freelancer">Freelancer</option>
          </select>
        </div>
        {error && <p className="text-danger">{error}</p>}
        <button type="submit" className="btn btn-primary">Sign Up</button>
      </form>
    </div>
  );
}

export default SignUp;
