import React, { useState, useEffect } from 'react';
import { db } from '../firebase';

function SearchTalent() {
  const [talent, setTalent] = useState([]);
  const [searchParams, setSearchParams] = useState({ category: '', skill: '' });

  const handleSearch = () => {
    db.collection('users')
      .where('role', '==', 'freelancer')
      .where('category', '==', searchParams.category)
      .where('skills', 'array-contains', searchParams.skill)
      .get()
      .then((snapshot) => {
        setTalent(snapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() })));
      });
  };

  return (
    <div>
      <input placeholder="Category" onChange={(e) => setSearchParams({ ...searchParams, category: e.target.value })} />
      <input placeholder="Skill" onChange={(e) => setSearchParams({ ...searchParams, skill: e.target.value })} />
      <button onClick={handleSearch}>Search</button>
      <ul>
        {talent.map((person) => (
          <li key={person.id}>{person.name}</li>
        ))}
      </ul>
    </div>
  );
}

export default SearchTalent;
