import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { doc, setDoc } from 'firebase/firestore';
import { auth, db } from '../firebase';
import './SignUp.css';

const SignUp = () => {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [accountType, setAccountType] = useState('client');
  const navigate = useNavigate();

  const handleSignUp = async (e) => {
    e.preventDefault();
    try {
      // Create user with Firebase Authentication
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
  
      // Save additional user data to Firestore
      await setDoc(doc(db, 'users', user.uid), {
        fullName,
        email,
        accountType,
        createdAt: new Date().toISOString(),
      }); // Add the missing closing parenthesis and semicolon
  
      console.log('User created and data saved to Firestore');
      navigate('/'); // Redirect to home page
    } catch (error) {
      console.error('Error signing up:', error.message);
    }
  };

  return (
    <div className="signup-container">
      <Link to="/" className="backButton">
        ‚Üê Back
      </Link>

      <h1 className="title">Create Account</h1>
      <p className="subtitle">Join BookMyService today</p>

      <div className="accountTypeContainer">
        <button
          className={`accountTypeButton ${accountType === 'client' ? 'active' : ''}`}
          onClick={() => setAccountType('client')}
        >
          I'm a Client
        </button>
        <button
          className={`accountTypeButton ${accountType === 'freelancer' ? 'active' : ''}`}
          onClick={() => setAccountType('freelancer')}
        >
          I'm a Freelancer
        </button>
      </div>

      <form className="form" onSubmit={handleSignUp}>
        <div className="inputContainer">
          <span className="inputIcon">üë§</span>
          <input
            type="text"
            placeholder="Full Name"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
            required
          />
        </div>

        <div className="inputContainer">
          <span className="inputIcon">‚úâÔ∏è</span>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div className="inputContainer">
          <span className="inputIcon">üîí</span>
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <button type="submit" className="signUpButton">
          Create Account
        </button>

        <p className="orText">OR</p>

        <div className="socialButtons">
          <button className="socialButton">Google</button>
          <button className="socialButton">Apple</button>
        </div>

        <p className="footer">
          Already have an account? <Link to="/SignIn">Sign In</Link>
        </p>
      </form>
    </div>
  );
};

export default SignUp;
