import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { auth } from '../firebase';
import './Header.css';

const Header = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Listen for authentication state changes
    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (user) {
        setUser(user); // User is signed in
      } else {
        setUser(null); // User is signed out
      }
    });

    // Cleanup subscription on unmount
    return () => unsubscribe();
  }, []);

  const handleProfileClick = () => {
    if (user) {
      // Navigate to the appropriate dashboard based on role
      const role = user.role; // Assuming role is stored in Firestore
      navigate(`/${role}-dashboard`);
    }
  };

  return (
    <header className="header">
      <div className="navTitle">Book My Service</div>
      <nav className="navLinks">
        <Link to="/FindFreelancers" className="navLink">
          Hire Freelancer
        </Link>
        <Link to="/FindJobs" className="navLink">
          Find Jobs
        </Link>
        <Link to="/how-it-works" className="navLink">
          How it Works
        </Link>
        <Link to="/about-us" className="navLink">
          About Us
        </Link>
      </nav>
      {user ? (
        <div className="profileIcon" onClick={handleProfileClick}>
          ðŸ‘¤
        </div>
      ) : (
        <Link to="/SignIn" className="loginButton">
          Login
        </Link>
      )}
    </header>
  );
};

export default Header;
