import React from 'react';
import './HowItWorks.css';

function HowItWorks() {
  const steps = [
    { title: 'Post a Project', description: 'Clients post projects with detailed requirements.', icon: 'üìã' },
    { title: 'Freelancers Bid', description: 'Service providers bid on projects with their proposals.', icon: 'üë®‚Äçüíª' },
    { title: 'Hire and Manage', description: 'Clients hire freelancers and manage their work.', icon: 'üõ†Ô∏è' },
    { title: 'Payment Processing', description: 'Secure payment is made after project completion.', icon: 'üí∞' }
  ];

  return (
    <section className="how-it-works">
      <h2>How It Works</h2>
      <div className="steps">
        {steps.map((step, index) => (
          <div className="step-item" key={index}>
            <div className="icon">{step.icon}</div>
            <h3>{step.title}</h3>
            <p>{step.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
}

export default HowItWorks;
