import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import "./Dashboard.css";

function Dashboard() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const userRole = queryParams.get("role"); // Get role from URL query params
  const [activeTab, setActiveTab] = useState("Overview");

  const renderContent = () => {
    switch (activeTab) {
      case "Overview":
        return (
          <div>
            <h3>Welcome to your {userRole === "client" ? "Client" : "Freelancer"} Dashboard!</h3>
            <p>Here you can manage your projects, payments, and profile.</p>
          </div>
        );
      case "Projects":
        return (
          <div>
            <h3>{userRole === "client" ? "My Projects" : "Projects Worked On"}</h3>
            <p>View and manage your projects here.</p>
          </div>
        );
      case "Inbox":
        return (
          <div>
            <h3>Inbox</h3>
            <p>Your messages and notifications will appear here.</p>
          </div>
        );
      case "Connections":
        return (
          <div>
            <h3>{userRole === "client" ? "Freelancers" : "Clients"}</h3>
            <p>
              {userRole === "client"
                ? "Freelancers you’ve worked with will be listed here."
                : "Clients you’ve worked with will be listed here."}
            </p>
          </div>
        );
      case "AccountSettings":
        return (
          <div>
            <h3>Account Settings</h3>
            <p>Manage your account details, preferences, and security settings.</p>
          </div>
        );
      default:
        return (
          <div>
            <h3>Welcome!</h3>
            <p>Select a section from the sidebar to view its content.</p>
          </div>
        );
    }
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <h3>Dashboard</h3>
        <ul>
          <li
            className={activeTab === "Overview" ? "active" : ""}
            onClick={() => setActiveTab("Overview")}
          >
            Overview
          </li>
          <li
            className={activeTab === "Projects" ? "active" : ""}
            onClick={() => setActiveTab("Projects")}
          >
            {userRole === "client" ? "My Projects" : "Projects Worked On"}
          </li>
          <li
            className={activeTab === "Inbox" ? "active" : ""}
            onClick={() => setActiveTab("Inbox")}
          >
            Inbox
          </li>
          <li
            className={activeTab === "Connections" ? "active" : ""}
            onClick={() => setActiveTab("Connections")}
          >
            {userRole === "client" ? "Freelancers" : "Clients"}
          </li>
          <li
            className={activeTab === "AccountSettings" ? "active" : ""}
            onClick={() => setActiveTab("AccountSettings")}
          >
            Account Settings
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="dashboard-content">
        <h2>{activeTab}</h2>
        <div className="content-area">{renderContent()}</div>
      </div>
    </div>
  );
}

export default Dashboard;
