import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { auth } from "../firebase";
import { doc, getDoc } from "firebase/firestore";
import { db } from "../firebase";
import "./Dashboard.css";

function Dashboard() {
  const [activeTab, setActiveTab] = useState("Overview");
  const [userRole, setUserRole] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserRole = async () => {
      const user = auth.currentUser;
      if (user) {
        const userDoc = await getDoc(doc(db, "users", user.uid));
        if (userDoc.exists()) {
          setUserRole(userDoc.data().accountType); // Set the user's role
        }
      }
    };

    fetchUserRole();
  }, []);

  const handleLogout = async () => {
    try {
      await auth.signOut();
      navigate("/");
    } catch (error) {
      console.error("Error logging out:", error.message);
    }
  };

  const renderContent = () => {
    switch (activeTab) {
      case "Overview":
        return <div>Welcome to your Dashboard! Here you can view your progress and manage your profile.</div>;
      case "Projects":
        return <div>Here are your projects (On Progress, Completed, Draft, Archived).</div>;
      case "Inbox":
        return <div>Your messages and notifications will appear here.</div>;
      case "Connections":
        return (
          <div>
            {userRole === "client"
              ? "Freelancers you’ve worked with will be listed here."
              : "Clients you’ve worked with will be listed here."}
          </div>
        );
      case "AccountSettings":
        return <div>Manage your account settings, including personal details and preferences.</div>;
      case "Support":
        return <div>Contact support for assistance.</div>;
      default:
        return <div>Select a section from the sidebar to view its content.</div>;
    }
  };

  if (!userRole) {
    return <div>Loading...</div>; // Show a loading state while fetching the role
  }

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <h3>Dashboard</h3>
        <ul>
          <li
            className={activeTab === "Overview" ? "active" : ""}
            onClick={() => setActiveTab("Overview")}
          >
            Overview
          </li>
          <li
            className={activeTab === "Projects" ? "active" : ""}
            onClick={() => setActiveTab("Projects")}
          >
            {userRole === "client" ? "My Projects" : "Projects Worked On"}
          </li>
          <li
            className={activeTab === "Inbox" ? "active" : ""}
            onClick={() => setActiveTab("Inbox")}
          >
            Inbox
          </li>
          <li
            className={activeTab === "Connections" ? "active" : ""}
            onClick={() => setActiveTab("Connections")}
          >
            {userRole === "client" ? "Freelancers" : "Clients"}
          </li>
          <li
            className={activeTab === "AccountSettings" ? "active" : ""}
            onClick={() => setActiveTab("AccountSettings")}
          >
            Account Settings
          </li>
          <li
            className={activeTab === "Support" ? "active" : ""}
            onClick={() => setActiveTab("Support")}
          >
            Support
          </li>
          <li onClick={handleLogout} className="logout">
            Log Out
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="dashboard-content">
        <h2>{activeTab}</h2>
        <div className="content-area">{renderContent()}</div>
      </div>
    </div>
  );
}

export default Dashboard;
