import React, { useState, useEffect } from 'react';
import { db, auth } from '../firebaseConfig';
import { useNavigate } from 'react-router-dom';

function AccountSetup() {
  const [userData, setUserData] = useState({
    skills: '',
    experience: '',
    rate: '',
    portfolio: '',
    documents: ''
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      const user = auth.currentUser;
      if (user) {
        const userDoc = await db.collection('users').doc(user.uid).get();
        if (userDoc.exists) {
          setUserData(userDoc.data());
        }
      }
    };
    fetchUserData();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSave = async (e) => {
    e.preventDefault();
    const user = auth.currentUser;
    if (user) {
      await db.collection('users').doc(user.uid).update(userData);
      navigate('/profile');  // Redirect to profile page after saving
    }
  };

  return (
    <div className="account-setup">
      <h1>Complete Your Account Setup</h1>
      <form onSubmit={handleSave}>
        <div className="form-group">
          <label>Skills (comma-separated)</label>
          <input
            type="text"
            name="skills"
            value={userData.skills}
            onChange={handleInputChange}
            placeholder="e.g., JavaScript, React"
          />
        </div>
        <div className="form-group">
          <label>Experience</label>
          <input
            type="text"
            name="experience"
            value={userData.experience}
            onChange={handleInputChange}
            placeholder="e.g., 5 years"
          />
        </div>
        <div className="form-group">
          <label>Hourly Rate</label>
          <input
            type="text"
            name="rate"
            value={userData.rate}
            onChange={handleInputChange}
            placeholder="e.g., $50/hr"
          />
        </div>
        <div className="form-group">
          <label>Portfolio Link</label>
          <input
            type="text"
            name="portfolio"
            value={userData.portfolio}
            onChange={handleInputChange}
            placeholder="e.g., http://portfolio.com"
          />
        </div>
        <div className="form-group">
          <label>Document Link (e.g., Resume)</label>
          <input
            type="text"
            name="documents"
            value={userData.documents}
            onChange={handleInputChange}
            placeholder="e.g., http://resume.com"
          />
        </div>
        <button type="submit" className="btn btn-primary">Save Profile</button>
      </form>
    </div>
  );
}

export default AccountSetup;
